import React, { useState } from "react";

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("");
  const [workloadCode, setWorkloadCode] = useState("");
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);
  const [showDownloadInfo, setShowDownloadInfo] = useState(false);


  const copyToClipboard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);
      // ÂèØ‰ª•Ê∑ªÂä†‰∏Ä‰∏™‰∏¥Êó∂ÁöÑÊàêÂäüÊèêÁ§∫
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const hasExtraOutput = (perfOutput) => {
    if (!perfOutput || perfOutput.trim() === '') return false;

    // ÁßªÈô§PERF_START:ÂíåPERF_END:ÔºåÂè™Ê£ÄÊü•‰∏≠Èó¥ÁöÑÂÜÖÂÆπ
    let content = perfOutput.trim();
    if (content.startsWith('PERF_START:')) {
      content = content.substring('PERF_START:'.length).trim();
    }
    if (content.endsWith('PERF_END:')) {
      content = content.substring(0, content.length - 'PERF_END:'.length).trim();
    }

    // Ê£ÄÊü•ÊòØÂê¶Âè™ÊúâÊ†áÂáÜÁöÑÂõõË°åËæìÂá∫
    const lines = content.split('\n').filter(line => line.trim() !== '');

    console.log('hasExtraOutput check:', {
      perfOutput: perfOutput,
      content: content,
      lines: lines,
      lineCount: lines.length
    });

    // Ê†áÂáÜËæìÂá∫Â∫îËØ•Âè™Êúâ4Ë°åÔºö+ python /tmp/workload.py, Mean:, Std Dev:, + echo
    if (lines.length === 4) {
      const hasPythonCmd = lines.some(line => line.includes('+ python /tmp/workload.py'));
      const hasMean = lines.some(line => line.startsWith('Mean:'));
      const hasStdDev = lines.some(line => line.startsWith('Std Dev:'));
      const hasEcho = lines.some(line => line.includes('+ echo'));

      console.log('Standard format check:', {
        hasPythonCmd,
        hasMean,
        hasStdDev,
        hasEcho,
        allMatch: hasPythonCmd && hasMean && hasStdDev && hasEcho
      });

      // Â¶ÇÊûúÊâÄÊúâÂõõË°åÈÉΩÁ¨¶ÂêàÊ†áÂáÜÊ†ºÂºèÔºåÂàôÊ≤°ÊúâÈ¢ùÂ§ñËæìÂá∫
      if (hasPythonCmd && hasMean && hasStdDev && hasEcho) {
        return false;
      }
    }

    // Â¶ÇÊûúË°åÊï∞‰∏çÊòØ4ÔºåÊàñËÄÖÂõõË°å‰∏≠Êúâ‰∏çÁ¨¶ÂêàÊ†áÂáÜÁöÑÔºåËØ¥ÊòéÊúâÈ¢ùÂ§ñËæìÂá∫
    return true;
  };

  const filterOutputForDisplay = (perfOutput) => {
    if (!perfOutput || perfOutput.trim() === '') return '';

    // ÁßªÈô§PERF_START:ÂíåPERF_END:ÔºåÂè™Â§ÑÁêÜ‰∏≠Èó¥ÁöÑÂÜÖÂÆπ
    let content = perfOutput.trim();
    if (content.startsWith('PERF_START:')) {
      content = content.substring('PERF_START:'.length).trim();
    }
    if (content.endsWith('PERF_END:')) {
      content = content.substring(0, content.length - 'PERF_END:'.length).trim();
    }

    const lines = content.split('\n');
    const filteredLines = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      if (line === '') continue;

      // Ë∑≥ËøáÊ†áÂáÜÁöÑÂõõË°åËæìÂá∫
      if (line.includes('+ python /tmp/workload.py') || 
          line.startsWith('Mean:') || 
          line.startsWith('Std Dev:') || 
          line.includes('+ echo')) {
        continue;
      }

      // ‰øùÁïôÂÖ∂‰ªñÊâÄÊúâË°åÔºà‰∏ªË¶ÅÊòØÈîôËØØ‰ø°ÊÅØÔºâ
      filteredLines.push(lines[i]);
    }

    return filteredLines.join('\n');
  };



  const detectMissingImports = (code) => {
    const missingImports = [];
    const lines = code.split('\n');

    // Ëß£ÊûêÁé∞ÊúâÁöÑÂØºÂÖ•
    const existingImports = new Set();
    const existingFromImports = new Map(); // module -> Set of imported items

    lines.forEach(line => {
      const trimmedLine = line.trim();

      // Ê£ÄÊµã import module
      const importMatch = trimmedLine.match(/^import\s+([a-zA-Z_][a-zA-Z0-9_.]*)/);
      if (importMatch) {
        const module = importMatch[1].split('.')[0];
        existingImports.add(module);
      }

      // Ê£ÄÊµã from module import items
      const fromImportMatch = trimmedLine.match(/^from\s+([a-zA-Z_][a-zA-Z0-9_.]*)\s+import\s+(.+)/);
      if (fromImportMatch) {
        const module = fromImportMatch[1].split('.')[0];
        const items = fromImportMatch[2].split(',').map(item => item.trim());

        if (!existingFromImports.has(module)) {
          existingFromImports.set(module, new Set());
        }
        items.forEach(item => {
          // Â§ÑÁêÜ "as alias" ÁöÑÊÉÖÂÜµ
          const cleanItem = item.split(' as ')[0].trim();
          existingFromImports.get(module).add(cleanItem);
        });
      }
    });

    // Ê£ÄÊµã‰ª£Á†Å‰∏≠‰ΩøÁî®ÁöÑÊ®°ÂùóÂíåÂáΩÊï∞
    const codeText = code.toLowerCase();

    // Ê£ÄÊµãnumpy‰ΩøÁî®
    if ((codeText.includes('np.') || codeText.includes('numpy.')) && 
        !existingImports.has('numpy') && 
        !existingFromImports.has('numpy')) {
      missingImports.push('forgetting import numpy');
    }

    // Ê£ÄÊµãastropy‰ΩøÁî® - Êõ¥Á≤æÁ°ÆÁöÑÊ£ÄÊµã
    if (codeText.includes('skycoord') || codeText.includes('u.deg') || codeText.includes('u.rad') || 
        codeText.includes('u.m') || codeText.includes('u.s') || codeText.includes('u.km') || 
        codeText.includes('u.arcsec') || codeText.includes('u.arcmin')) {

      // Ê£ÄÊü•ÊòØÂê¶Êúâastropy.unitsÁöÑÂØºÂÖ•
      const hasUnitsAsU = lines.some(line => 
        line.trim().toLowerCase().includes('import astropy.units as u') ||
        line.trim().toLowerCase().includes('from astropy import units as u')
      );

      if (!hasUnitsAsU) {
        missingImports.push('forgetting import astropy.units as u');
      }

              // Ê£ÄÊü•ÊòØÂê¶ÊúâSkyCoordÁöÑÂØºÂÖ•
        if (codeText.includes('skycoord')) {
          const hasSkyCoordImport = lines.some(line => {
            const lineLower = line.trim().toLowerCase();
            return lineLower.includes('skycoord') ||
                   lineLower.includes('from astropy.coordinates import skycoord') ||
                   lineLower.includes('from astropy.coordinates.sky_coordinate import skycoord') ||
                   lineLower.includes('from astropy.coordinates import sky_coordinate') ||
                   lineLower.includes('from astropy.coordinates.sky_coordinate import sky_coordinate');
          });

          if (!hasSkyCoordImport) {
            missingImports.push('forgetting import SkyCoord from astropy.coordinates');
          }
        }
    }

    // Ê£ÄÊµãscipy‰ΩøÁî®
    if (codeText.includes('scipy.') || codeText.includes('scipy_')) {
      if (!existingImports.has('scipy') && !existingFromImports.has('scipy')) {
        missingImports.push('forgetting import scipy');
      }
    }

    // Ê£ÄÊµãpandas‰ΩøÁî®
    if (codeText.includes('pd.') || codeText.includes('pandas.') || codeText.includes('dataframe')) {
      if (!existingImports.has('pandas') && !existingFromImports.has('pandas')) {
        missingImports.push('forgetting import pandas');
      }
    }

    // Ê£ÄÊµãmatplotlib‰ΩøÁî®
    if (codeText.includes('plt.') || codeText.includes('matplotlib.') || codeText.includes('pyplot')) {
      if (!existingImports.has('matplotlib') && !existingFromImports.has('matplotlib')) {
        missingImports.push('forgetting import matplotlib');
      }
    }

    // Ê£ÄÊµãÂÖ∂‰ªñÂ∏∏ËßÅÂ∫ì
    const commonLibraries = [
      { name: 'math', patterns: ['math.', 'math('] },
      { name: 'random', patterns: ['random.', 'random('] },
      { name: 'json', patterns: ['json.', 'json('] },
      { name: 'os', patterns: ['os.', 'os('] },
      { name: 'sys', patterns: ['sys.', 'sys('] },
      { name: 're', patterns: ['re.', 're('] },
      { name: 'datetime', patterns: ['datetime.', 'datetime('] },
      { name: 'collections', patterns: ['collections.', 'collections('] },
      { name: 'itertools', patterns: ['itertools.', 'itertools('] },
      { name: 'functools', patterns: ['functools.', 'functools('] }
    ];

    commonLibraries.forEach(lib => {
      const isUsed = lib.patterns.some(pattern => codeText.includes(pattern));
      if (isUsed && !existingImports.has(lib.name) && !existingFromImports.has(lib.name)) {
        missingImports.push(`forgetting import ${lib.name}`);
      }
    });

    return missingImports;
  };

  const clearAllInputs = () => {
    setPrUrl("");
    setWorkloadCode("");
    setCodeValidationErrors([]);
    // ‰∏çÊ∏ÖÈô§ result Âíå errorÔºå‰øùÊåÅËæìÂá∫ÊòæÁ§∫
  };

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "Cannot connect to Docker check service",
        setup_instructions: ["Please ensure Docker permission check service is running"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
    if (!dockerStatus?.permissions) {
      setError("Docker permissions not configured, please check Docker status first");
      return;
    }

    setRunning(true);
    setResult(null);
    setError(null);
    try {
      const res = await fetch("http://localhost:5678/run_benchmark", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({
          pr_url: prUrl,
          workload_code: workloadCode,
        }),
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      setError("Run failed: " + e);
    } finally {
      setRunning(false);
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg, #A51C30 0%, #8B0000 50%, #660000 100%)",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      position: "relative",
      overflowX: "hidden",
      overflowY: "auto",
      backgroundAttachment: "fixed",
      overscrollBehavior: "none"
    }}>

      {/* Header with Logo */}
      <div style={{
        background: "white",
        borderBottom: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        boxShadow: "0 4px 30px rgba(165, 28, 48, 0.15)",
        position: "relative",
        zIndex: 10
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px", display: "flex", alignItems: "center", justifyContent: "space-between"}}>
          <div style={{display: "flex", alignItems: "center", gap: "20px", marginLeft: "-120px"}}>
            <div style={{
              width: "236px",
              height: "80px",
              borderRadius: "12px",
              overflow: "hidden",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              background: "white"
            }}>
              <img 
                src="/Edge Computing Lab Logo.png" 
                alt="Edge Computing Lab Logo"
                style={{
                  maxWidth: "100%",
                  maxHeight: "100%",
                  objectFit: "contain",
                  width: "auto",
                  height: "auto"
                }}
              />
            </div>
            <div>
              <h1 style={{
                margin: 0,
                fontSize: "32px",
                fontWeight: "800",
                color: "#A51C30",
                letterSpacing: "-0.5px"
              }}>
                Performance Benchmark Platform
              </h1>
              <p style={{
                margin: "4px 0 0 0",
                fontSize: "20px",
                color: "#666",
                fontWeight: "600"
              }}>
                Edge Computing Lab
              </p>
              <p style={{
                margin: "2px 0 0 0",
                fontSize: "12px",
                color: "#A51C30",
                fontWeight: "600",
                fontStyle: "italic"
              }}>
                Harvard John A. Paulson School of Engineering and Applied Sciences
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{maxWidth: 1200, margin: "0 auto", padding: "40px 20px", position: "relative"}}>
        {/* Docker Status Check Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "30px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <h3 style={{
            margin: "0 0 20px 0",
            fontSize: "22px",
            fontWeight: "700",
            color: "#A51C30",
            display: "flex",
            alignItems: "center",
            gap: "10px"
          }}>
            <span style={{fontSize: "24px"}}>üîß</span>
            Docker Status Check
          </h3>
          <button 
            onClick={checkDockerStatus} 
            disabled={checkingDocker}
            style={{
              padding: "14px 28px",
              marginBottom: "20px",
              background: checkingDocker ? "#e0e0e0" : "linear-gradient(45deg, #A51C30, #8B0000)",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "15px",
              fontWeight: "600",
              cursor: checkingDocker ? "not-allowed" : "pointer",
              transition: "all 0.3s ease",
              boxShadow: checkingDocker ? "none" : "0 6px 20px rgba(165, 28, 48, 0.4)",
              border: "1px solid rgba(165, 28, 48, 0.2)"
            }}
          >
            {checkingDocker ? "Checking..." : "Check Docker Status"}
          </button>

          {dockerStatus && (
            <div>
              <div style={{marginBottom: "15px"}}>
                <strong style={{color: "#333"}}>Status: </strong>
                <span style={{
                  color: dockerStatus.permissions ? "#059669" : "#DC2626",
                  fontWeight: "600",
                  padding: "6px 14px",
                  borderRadius: "20px",
                  background: dockerStatus.permissions ? "rgba(5, 150, 105, 0.1)" : "rgba(220, 38, 38, 0.1)",
                  fontSize: "14px",
                  border: "1px solid",
                  borderColor: dockerStatus.permissions ? "rgba(5, 150, 105, 0.3)" : "rgba(220, 38, 38, 0.3)"
                }}>
                  {dockerStatus.permissions ? "‚úÖ Normal" : "‚ùå Configuration Required"}
                </span>
              </div>
              <div style={{marginBottom: "15px", color: "#666"}}>
                <strong>Message: </strong>{dockerStatus.message}
              </div>
              {dockerStatus.setup_instructions.length > 0 && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "12px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <strong style={{color: "#A51C30"}}>Setup Instructions:</strong>
                  <ul style={{margin: "10px 0", paddingLeft: "20px", color: "#666"}}>
                    {dockerStatus.setup_instructions.map((instruction, index) => (
                      <li key={index} style={{marginBottom: "5px"}}>{instruction}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Main Form */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "40px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <div style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "30px"
          }}>
            <h2 style={{
              margin: 0,
              fontSize: "28px",
              fontWeight: "800",
              color: "#A51C30",
              letterSpacing: "-0.5px"
            }}>
              Performance Benchmark Configuration
            </h2>
            <button 
              onClick={clearAllInputs}
              disabled={running}
              style={{
                padding: "10px 20px",
                background: running ? "#e0e0e0" : "linear-gradient(45deg, #666, #888)",
                color: "white",
                border: "none",
                borderRadius: "10px",
                fontSize: "14px",
                fontWeight: "600",
                cursor: running ? "not-allowed" : "pointer",
                transition: "all 0.3s ease",
                boxShadow: running ? "none" : "0 4px 15px rgba(0, 0, 0, 0.2)"
              }}
              onMouseOver={(e) => !running && (e.target.style.background = "linear-gradient(45deg, #555, #777)")}
              onMouseOut={(e) => !running && (e.target.style.background = "linear-gradient(45deg, #666, #888)")}
            >
              Clear All
            </button>
          </div>

          <div style={{display: "grid", gap: "25px"}}>
            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
                GitHub PR URL:
              </label>
              <input 
                type="text"
                style={{
                  width: "100%",
                  padding: "14px 18px",
                  border: "2px solid rgba(165, 28, 48, 0.2)",
                  borderRadius: "12px",
                  fontSize: "14px",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1,
                  outline: "none"
                }}
                value={prUrl} 
                onChange={e=>setPrUrl(e.target.value)} 
                disabled={running}
                onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
              />
            </div>

            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
