                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap", gap: "10px"}}>
                    <span style={{flex: "1", minWidth: "200px"}}>üì• Image Download Info:</span>
                    <div style={{display: "flex", gap: "8px"}}>
                      <button 
                        onClick={() => setShowDownloadInfo(!showDownloadInfo)}
                        style={{
                          padding: "6px 12px",
                          background: showDownloadInfo ? "#666" : "#A51C30",
                          color: "white",
                          border: "none",
                          borderRadius: "6px",
                          fontSize: "12px",
                          cursor: "pointer",
                          transition: "all 0.2s ease",
                          whiteSpace: "nowrap"
                        }}
                        onMouseOver={(e) => e.target.style.background = showDownloadInfo ? "#555" : "#8B0000"}
                        onMouseOut={(e) => e.target.style.background = showDownloadInfo ? "#666" : "#A51C30"}
                      >
                        {showDownloadInfo ? "üëÅÔ∏è Hide" : "üëÅÔ∏è Show"}
                      </button>
                      <button 
                        onClick={() => copyToClipboard(result.download_info)}
                        style={{
                          padding: "6px 12px",
                          background: "#A51C30",
                          color: "white",
                          border: "none",
                          borderRadius: "6px",
                          fontSize: "12px",
                          cursor: "pointer",
                          transition: "all 0.2s ease",
                          whiteSpace: "nowrap"
                        }}
                        onMouseOver={(e) => e.target.style.background = "#8B0000"}
                        onMouseOut={(e) => e.target.style.background = "#A51C30"}
                      >
                        üìã Copy
                      </button>
                    </div>
                  </div>
                  {showDownloadInfo && (
                    <div style={{
                      background: "#f8fafc",
                      padding: "16px",
                      borderRadius: "12px",
                      fontSize: "13px",
                      fontFamily: "'Fira Code', monospace",
                      border: "1px solid #e2e8f0",
                      overflowX: "auto",
                      whiteSpace: "pre-wrap",
                      wordBreak: "break-all",
                      maxHeight: "400px",
                      overflowY: "auto"
                    }}>
                      {result.download_info}
                    </div>
                  )}
                </div>
              )}

              <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    üìä Initial Results:
                  </div>
                  {result.mean_before && result.std_before ? (
                    <div style={{
                      background: "white",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid #e2e8f0"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_before.toFixed(6)}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_before.toFixed(6)}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.before}
                    </div>
                  )}
                </div>

                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    üìä Results After Patch:
                  </div>
                  {result.mean_after && result.std_after ? (
                    <div style={{
                      background: "white",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid #e2e8f0"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_after.toFixed(6)}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_after.toFixed(6)}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.after}
                    </div>
                  )}
                </div>
              </div>

              {/* ÊòæÁ§∫ÂÆåÊï¥ÁöÑPERF_STARTÂà∞PERF_ENDÂÜÖÂÆπÔºàÂ¶ÇÊûúÊúâÈ¢ùÂ§ñËæìÂá∫Ôºâ */}
              {(result.perf_output_before && hasExtraOutput(result.perf_output_before)) && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>üìã Initial Results (Full Output):</span>
                    <button 
                      onClick={() => copyToClipboard(result.perf_output_before)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      üìã Copy
                    </button>
                  </div>
                  <div style={{
                    background: "white",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid #e2e8f0",
                    overflowX: "auto",
                    whiteSpace: "pre-wrap",
                    maxHeight: "300px",
                    overflowY: "auto"
                  }}>
                    {filterOutputForDisplay(result.perf_output_before)}
                  </div>
                </div>
              )}

              {(result.perf_output_after && hasExtraOutput(result.perf_output_after)) && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>üìã Results After Patch (Full Output):</span>
                    <button 
                      onClick={() => copyToClipboard(result.perf_output_after)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      üìã Copy
                    </button>
                  </div>
                  <div style={{
                    background: "white",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid #e2e8f0",
                    overflowX: "auto",
                    whiteSpace: "pre-wrap",
                    maxHeight: "300px",
                    overflowY: "auto"
                  }}>
                    {filterOutputForDisplay(result.perf_output_after)}
                  </div>
                </div>
              )}

              {result.ratio && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px", display: "flex", justifyContent: "space-between", alignItems: "center"}}>
                    <span>üìà Performance Comparison:</span>
                    <button 
                      onClick={() => copyToClipboard(`Performance Ratio: ${result.ratio.toFixed(4)} (${result.ratio < 1 ? "Faster" : result.ratio > 1 ? "Slower" : "Same"})`)}
                      style={{
                        padding: "6px 12px",
                        background: "#A51C30",
                        color: "white",
                        border: "none",
                        borderRadius: "6px",
                        fontSize: "12px",
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseOver={(e) => e.target.style.background = "#8B0000"}
                      onMouseOut={(e) => e.target.style.background = "#A51C30"}
                    >
                      üìã Copy
                    </button>
                  </div>
                  <div style={{
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "16px",
                    fontWeight: "700",
                    textAlign: "center",
                    background: highlightColor(result.ratio) === "green" ? "rgba(5, 150, 105, 0.1)" :
                                highlightColor(result.ratio) === "red" ? "rgba(220, 38, 38, 0.1)" : "rgba(107, 114, 128, 0.1)",
                    color: highlightColor(result.ratio) === "green" ? "#059669" :
                           highlightColor(result.ratio) === "red" ? "#DC2626" : "#6B7280",
                    border: "2px solid",
                    borderColor: highlightColor(result.ratio) === "green" ? "rgba(5, 150, 105, 0.3)" :
                                 highlightColor(result.ratio) === "red" ? "rgba(220, 38, 38, 0.3)" : "rgba(107, 114, 128, 0.3)"
                  }}>
                    Performance Ratio: {result.ratio.toFixed(4)} ({result.ratio < 1 ? "üöÄ Faster" : result.ratio > 1 ? "üêå Slower" : "‚û°Ô∏è Same"})
                  </div>
                </div>
              )}

              {result.error && (
                <div style={{
                  background: "rgba(220, 38, 38, 0.1)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "2px solid rgba(220, 38, 38, 0.3)",
                  boxShadow: "0 4px 15px rgba(220, 38, 38, 0.2)"
                }}>
