import React, { useState } from "react";

const defaultImports = `import numpy as np
import astropy.units as u
from astropy.coordinates.sky_coordinate import SkyCoord`;
const defaultSetup = `global c
c = SkyCoord(np.arange(30.)*u.deg, np.arange(30.)*u.deg)`;
const defaultWorkload = `global c
c[1:2]`;

function highlightColor(ratio: number) {
  if (ratio < 1) return "green";
  if (ratio > 1) return "red";
  return "grey";
}

const App = () => {
  const [prUrl, setPrUrl] = useState("https://github.com/astropy/astropy/pull/6940");
  const [imports, setImports] = useState(defaultImports);
  const [setup, setSetup] = useState(defaultSetup);
  const [workload, setWorkload] = useState(defaultWorkload);
  const [number, setNumber] = useState(100);
  const [repeat, setRepeat] = useState(200);
  const [running, setRunning] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dockerStatus, setDockerStatus] = useState(null);
  const [checkingDocker, setCheckingDocker] = useState(false);

  const checkDockerStatus = async () => {
    setCheckingDocker(true);
    try {
      const res = await fetch("http://localhost:5679/check_docker");
      const data = await res.json();
      setDockerStatus(data);
    } catch (e) {
      setDockerStatus({
        installed: false,
        running: false,
        permissions: false,
        message: "Cannot connect to Docker check service",
        setup_instructions: ["Please ensure Docker permission check service is running"]
      });
    } finally {
      setCheckingDocker(false);
    }
  };

  const handleRun = async () => {
    if (!dockerStatus?.permissions) {
      setError("Docker permissions not configured, please check Docker status first");
      return;
    }

    setRunning(true);
    setResult(null);
    setError(null);
    try {
      const res = await fetch("http://localhost:5678/run_benchmark", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({
          pr_url: prUrl,
          imports,
          setup,
          workload,
          number,
          repeat,
        }),
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      setError("Run failed: " + e);
    } finally {
      setRunning(false);
    }
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "linear-gradient(135deg, #A51C30 0%, #8B0000 50%, #660000 100%)",
      fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
      position: "relative"
    }}>
      {/* Background Pattern */}
      <div style={{
        position: "absolute",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundImage: `
          radial-gradient(circle at 20% 80%, rgba(255,255,255,0.1) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(255,255,255,0.05) 0%, transparent 50%),
          radial-gradient(circle at 40% 40%, rgba(255,255,255,0.03) 0%, transparent 50%)
        `,
        pointerEvents: "none"
      }} />

      {/* Header with Logo */}
      <div style={{
        background: "white",
        borderBottom: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        boxShadow: "0 4px 30px rgba(165, 28, 48, 0.15)",
        position: "relative",
        zIndex: 10
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px", display: "flex", alignItems: "center", justifyContent: "space-between"}}>
          <div style={{display: "flex", alignItems: "center", gap: "20px"}}>
            <div style={{
              width: "200px",
              height: "120px",
              borderRadius: "12px",
              overflow: "hidden",
              boxShadow: "0 4px 20px rgba(165, 28, 48, 0.3)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              background: "white"
            }}>
              <img 
                src="/Edge Computing Lab Logo.png" 
                alt="Edge Computing Lab Logo"
                style={{
                  maxWidth: "100%",
                  maxHeight: "100%",
                  objectFit: "contain",
                  width: "auto",
                  height: "auto"
                }}
              />
            </div>
            <div>
              <h1 style={{
                margin: 0,
                fontSize: "32px",
                fontWeight: "800",
                color: "#A51C30",
                letterSpacing: "-0.5px"
              }}>
                Edge Computing Lab
              </h1>
              <p style={{
                margin: "4px 0 0 0",
                fontSize: "16px",
                color: "#666",
                fontWeight: "500"
              }}>
                Performance Benchmark Platform
              </p>
              <p style={{
                margin: "2px 0 0 0",
                fontSize: "12px",
                color: "#A51C30",
                fontWeight: "600",
                fontStyle: "italic"
              }}>
                Harvard University
              </p>
            </div>
          </div>
          <div style={{
            fontSize: "12px",
            color: "#A51C30",
            textAlign: "right",
            fontStyle: "italic",
            fontWeight: "600",
            padding: "8px 16px",
            background: "rgba(165, 28, 48, 0.1)",
            borderRadius: "20px",
            border: "1px solid rgba(165, 28, 48, 0.2)"
          }}>
            Research Property
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{maxWidth: 1200, margin: "0 auto", padding: "40px 20px", position: "relative", zIndex: 5}}>
        {/* Docker Status Check Section */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "30px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <h3 style={{
            margin: "0 0 20px 0",
            fontSize: "22px",
            fontWeight: "700",
            color: "#A51C30",
            display: "flex",
            alignItems: "center",
            gap: "10px"
          }}>
            <span style={{fontSize: "24px"}}>üîß</span>
            Docker Status Check
          </h3>
          <button 
            onClick={checkDockerStatus} 
            disabled={checkingDocker}
            style={{
              padding: "14px 28px",
              marginBottom: "20px",
              background: checkingDocker ? "#e0e0e0" : "linear-gradient(45deg, #A51C30, #8B0000)",
              color: "white",
              border: "none",
              borderRadius: "12px",
              fontSize: "15px",
              fontWeight: "600",
              cursor: checkingDocker ? "not-allowed" : "pointer",
              transition: "all 0.3s ease",
              boxShadow: checkingDocker ? "none" : "0 6px 20px rgba(165, 28, 48, 0.4)",
              border: "1px solid rgba(165, 28, 48, 0.2)"
            }}
          >
            {checkingDocker ? "Checking..." : "Check Docker Status"}
          </button>

          {dockerStatus && (
            <div>
              <div style={{marginBottom: "15px"}}>
                <strong style={{color: "#333"}}>Status: </strong>
                <span style={{
                  color: dockerStatus.permissions ? "#059669" : "#DC2626",
                  fontWeight: "600",
                  padding: "6px 14px",
                  borderRadius: "20px",
                  background: dockerStatus.permissions ? "rgba(5, 150, 105, 0.1)" : "rgba(220, 38, 38, 0.1)",
                  fontSize: "14px",
                  border: "1px solid",
                  borderColor: dockerStatus.permissions ? "rgba(5, 150, 105, 0.3)" : "rgba(220, 38, 38, 0.3)"
                }}>
                  {dockerStatus.permissions ? "‚úÖ Normal" : "‚ùå Configuration Required"}
                </span>
              </div>
              <div style={{marginBottom: "15px", color: "#666"}}>
                <strong>Message: </strong>{dockerStatus.message}
              </div>
              {dockerStatus.setup_instructions.length > 0 && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "12px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <strong style={{color: "#A51C30"}}>Setup Instructions:</strong>
                  <ul style={{margin: "10px 0", paddingLeft: "20px", color: "#666"}}>
                    {dockerStatus.setup_instructions.map((instruction, index) => (
                      <li key={index} style={{marginBottom: "5px"}}>{instruction}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Main Form */}
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          backdropFilter: "blur(20px)",
          borderRadius: "20px",
          padding: "40px",
          marginBottom: "30px",
          boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
          border: "1px solid rgba(165, 28, 48, 0.1)"
        }}>
          <h2 style={{
            margin: "0 0 30px 0",
            fontSize: "28px",
            fontWeight: "800",
            color: "#A51C30",
            textAlign: "center",
            letterSpacing: "-0.5px"
          }}>
            üöÄ Performance Benchmark Configuration
          </h2>

          <div style={{display: "grid", gap: "25px"}}>
            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
                GitHub PR URL:
              </label>
              <input 
                style={{
                  width: "100%",
                  padding: "14px 18px",
                  border: "2px solid rgba(165, 28, 48, 0.2)",
                  borderRadius: "12px",
                  fontSize: "14px",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1,
                  outline: "none"
                }}
                value={prUrl} 
                onChange={e=>setPrUrl(e.target.value)} 
                disabled={running}
                placeholder="https://github.com/org/repo/pull/123"
                onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
              />
            </div>

            <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
              <div>
                <label style={{
                  display: "block",
                  marginBottom: "10px",
                  fontSize: "15px",
                  fontWeight: "600",
                  color: "#A51C30"
                }}>
                  Imports:
                </label>
                <textarea 
                  rows={4}
                  style={{
                    width: "100%",
                    padding: "14px 18px",
                    border: "2px solid rgba(165, 28, 48, 0.2)",
                    borderRadius: "12px",
                    fontSize: "14px",
                    fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                    resize: "vertical",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1,
                    outline: "none"
                  }}
                  value={imports} 
                  onChange={e=>setImports(e.target.value)} 
                  disabled={running}
                  placeholder="import numpy as np"
                  onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                  onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
                />
              </div>
              <div>
                <label style={{
                  display: "block",
                  marginBottom: "10px",
                  fontSize: "15px",
                  fontWeight: "600",
                  color: "#A51C30"
                }}>
                  setup():
                </label>
                <textarea 
                  rows={4}
                  style={{
                    width: "100%",
                    padding: "14px 18px",
                    border: "2px solid rgba(165, 28, 48, 0.2)",
                    borderRadius: "12px",
                    fontSize: "14px",
                    fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                    resize: "vertical",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1,
                    outline: "none"
                  }}
                  value={setup} 
                  onChange={e=>setSetup(e.target.value)} 
                  disabled={running}
                  placeholder="global c\nc = np.arange(100)"
                  onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                  onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
                />
              </div>
            </div>

            <div>
              <label style={{
                display: "block",
                marginBottom: "10px",
                fontSize: "15px",
                fontWeight: "600",
                color: "#A51C30"
              }}>
                workload():
              </label>
              <textarea 
                rows={4}
                style={{
                  width: "100%",
                  padding: "14px 18px",
                  border: "2px solid rgba(165, 28, 48, 0.2)",
                  borderRadius: "12px",
                  fontSize: "14px",
                  fontFamily: "'Fira Code', 'Monaco', 'Consolas', monospace",
                  resize: "vertical",
                  transition: "all 0.3s ease",
                  background: running ? "#f9fafb" : "white",
                  opacity: running ? 0.6 : 1,
                  outline: "none"
                }}
                value={workload} 
                onChange={e=>setWorkload(e.target.value)} 
                disabled={running}
                placeholder="global c\nc * 2"
                onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
              />
            </div>

            <div style={{display: "flex", gap: "20px", alignItems: "center"}}>
              <div style={{flex: 1}}>
                <label style={{
                  display: "block",
                  marginBottom: "10px",
                  fontSize: "15px",
                  fontWeight: "600",
                  color: "#A51C30"
                }}>
                  Number:
                </label>
                <input 
                  type="number"
                  style={{
                    width: "100%",
                    padding: "14px 18px",
                    border: "2px solid rgba(165, 28, 48, 0.2)",
                    borderRadius: "12px",
                    fontSize: "14px",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1,
                    outline: "none"
                  }}
                  value={number} 
                  onChange={e=>setNumber(Number(e.target.value))} 
                  disabled={running}
                  onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                  onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
                />
              </div>
              <div style={{flex: 1}}>
                <label style={{
                  display: "block",
                  marginBottom: "10px",
                  fontSize: "15px",
                  fontWeight: "600",
                  color: "#A51C30"
                }}>
                  Repeat:
                </label>
                <input 
                  type="number"
                  style={{
                    width: "100%",
                    padding: "14px 18px",
                    border: "2px solid rgba(165, 28, 48, 0.2)",
                    borderRadius: "12px",
                    fontSize: "14px",
                    transition: "all 0.3s ease",
                    background: running ? "#f9fafb" : "white",
                    opacity: running ? 0.6 : 1,
                    outline: "none"
                  }}
                  value={repeat} 
                  onChange={e=>setRepeat(Number(e.target.value))} 
                  disabled={running}
                  onFocus={(e) => e.target.style.borderColor = "#A51C30"}
                  onBlur={(e) => e.target.style.borderColor = "rgba(165, 28, 48, 0.2)"}
                />
              </div>
            </div>

            <div style={{textAlign: "center", marginTop: "20px"}}>
              <button 
                style={{
                  padding: "18px 50px",
                  background: running ? "#e0e0e0" : "linear-gradient(45deg, #A51C30, #8B0000)",
                  color: "white",
                  border: "none",
                  borderRadius: "16px",
                  fontSize: "18px",
                  fontWeight: "700",
                  cursor: running ? "not-allowed" : "pointer",
                  transition: "all 0.3s ease",
                  boxShadow: running ? "none" : "0 10px 30px rgba(165, 28, 48, 0.4)",
                  minWidth: "250px",
                  border: "1px solid rgba(165, 28, 48, 0.3)"
                }}
                onClick={handleRun} 
                disabled={running}
              >
                {running ? "‚è≥ Running..." : "üöÄ Run Benchmark"}
              </button>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {result && (
          <div style={{
            background: "rgba(255, 255, 255, 0.95)",
            backdropFilter: "blur(20px)",
            borderRadius: "20px",
            padding: "40px",
            marginBottom: "30px",
            boxShadow: "0 8px 40px rgba(165, 28, 48, 0.15)",
            border: "1px solid rgba(165, 28, 48, 0.1)"
          }}>
            <h3 style={{
              margin: "0 0 30px 0",
              fontSize: "26px",
              fontWeight: "800",
              color: "#A51C30",
              textAlign: "center",
              letterSpacing: "-0.5px"
            }}>
              üìä Benchmark Results
            </h3>

            <div style={{display: "grid", gap: "25px"}}>
              <div style={{
                background: "rgba(165, 28, 48, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(165, 28, 48, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "8px"}}>
                  üê≥ Image Name:
                </div>
                <div style={{fontFamily: "'Fira Code', monospace", fontSize: "14px"}}>
                  {result.image_tag}
                </div>
              </div>

              <div style={{
                background: "rgba(165, 28, 48, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(165, 28, 48, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                  üìã Complete Docker Command:
                </div>
                <div style={{
                  background: "#f8fafc",
                  padding: "16px",
                  borderRadius: "12px",
                  fontSize: "13px",
                  fontFamily: "'Fira Code', monospace",
                  border: "1px solid #e2e8f0",
                  overflowX: "auto"
                }}>
                  {result.docker_command}
                </div>
                <div style={{fontSize: "12px", color: "#64748b", marginTop: "8px"}}>
                  Note: Replace &lt;REPLACE_ME&gt; with the full path to your workload.py file
                </div>
              </div>

              <div style={{
                background: "rgba(165, 28, 48, 0.05)",
                padding: "20px",
                borderRadius: "16px",
                border: "1px solid rgba(165, 28, 48, 0.2)"
              }}>
                <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                  üìù Auto-generated workload.py:
                </div>
                <pre style={{
                  background: "#f8fafc",
                  padding: "16px",
                  borderRadius: "12px",
                  fontSize: "13px",
                  fontFamily: "'Fira Code', monospace",
                  border: "1px solid #e2e8f0",
                  overflowX: "auto",
                  margin: 0
                }}>
                  {result.workload_code}
                </pre>
              </div>

              {result.download_info && (
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    üì• Image Download Info:
                  </div>
                  <div style={{
                    background: "rgba(165, 28, 48, 0.1)",
                    padding: "16px",
                    borderRadius: "12px",
                    fontSize: "13px",
                    fontFamily: "'Fira Code', monospace",
                    border: "1px solid rgba(165, 28, 48, 0.3)"
                  }}>
                    <div style={{color: "#A51C30", fontWeight: "bold", marginBottom: "8px"}}>
                      üì• New Image Downloaded
                    </div>
                    <pre style={{
                      margin: 0,
                      whiteSpace: "pre-wrap",
                      fontSize: "12px"
                    }}>
                      {result.download_info}
                    </pre>
                  </div>
                </div>
              )}

              <div style={{display: "grid", gridTemplateColumns: "1fr 1fr", gap: "20px"}}>
                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(165, 28, 48, 0.2)"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    üìà Initial Results:
                  </div>
                  {result.mean_before && result.std_before ? (
                    <div style={{
                      background: "rgba(165, 28, 48, 0.1)",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid rgba(165, 28, 48, 0.3)"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_before}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_before}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.before}
                    </div>
                  )}
                </div>

                <div style={{
                  background: "rgba(165, 28, 48, 0.05)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "2px solid #A51C30"
                }}>
                  <div style={{fontWeight: "600", color: "#A51C30", marginBottom: "12px"}}>
                    üéØ Results After Patch:
                  </div>
                  {result.mean_after && result.std_after ? (
                    <div style={{
                      background: "rgba(165, 28, 48, 0.1)",
                      padding: "16px",
                      borderRadius: "12px",
                      border: "1px solid rgba(165, 28, 48, 0.3)"
                    }}>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Mean: {result.mean_after}
                      </div>
                      <div style={{fontSize: "14px", fontWeight: "600"}}>
                        Std: {result.std_after}
                      </div>
                    </div>
                  ) : (
                    <div style={{color: "#DC2626", fontWeight: "bold"}}>
                      {result.after}
                    </div>
                  )}
                </div>
              </div>

              {result.ratio && (
                <div style={{
                  padding: "20px",
                  borderRadius: "16px",
                  background: highlightColor(result.ratio) === "green" ? "rgba(34, 197, 94, 0.1)" :
                              highlightColor(result.ratio) === "red" ? "rgba(239, 68, 68, 0.1)" : "rgba(156, 163, 175, 0.1)",
                  border: "2px solid",
                  borderColor: highlightColor(result.ratio) === "green" ? "#22c55e" :
                              highlightColor(result.ratio) === "red" ? "#ef4444" : "#9ca3af",
                  textAlign: "center"
                }}>
                  <div style={{
                    fontSize: "18px",
                    fontWeight: "700",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280"
                  }}>
                    Performance Change Ratio: {result.ratio.toFixed(4)}
                  </div>
                  <div style={{
                    fontSize: "16px",
                    fontWeight: "600",
                    color: highlightColor(result.ratio) === "green" ? "#22c55e" :
                           highlightColor(result.ratio) === "red" ? "#ef4444" : "#6b7280",
                    marginTop: "8px"
                  }}>
                    {result.ratio < 1 ? "üöÄ Faster" : result.ratio > 1 ? "üêå Slower" : "‚öñÔ∏è Same"}
                  </div>
                </div>
              )}

              {result.error && (
                <div style={{
                  background: "rgba(239, 68, 68, 0.1)",
                  padding: "20px",
                  borderRadius: "16px",
                  border: "1px solid rgba(239, 68, 68, 0.3)",
                  color: "#ef4444",
                  fontWeight: "600"
                }}>
                  ‚ùå Error: {result.error}
                </div>
              )}
            </div>
          </div>
        )}

        {error && (
          <div style={{
            background: "rgba(239, 68, 68, 0.1)",
            padding: "20px",
            borderRadius: "16px",
            border: "1px solid rgba(239, 68, 68, 0.3)",
            color: "#ef4444",
            fontWeight: "600",
            textAlign: "center"
          }}>
            ‚ùå {error}
          </div>
        )}
      </div>

      {/* Footer */}
      <div style={{
        background: "rgba(255, 255, 255, 0.95)",
        backdropFilter: "blur(20px)",
        borderTop: "1px solid rgba(165, 28, 48, 0.2)",
        padding: "20px 0",
        textAlign: "center",
        color: "#A51C30",
        fontSize: "14px",
        fontWeight: "600"
      }}>
        <div style={{maxWidth: 1200, margin: "0 auto", padding: "0 20px"}}>
          ¬© 2024 Edge Computing Lab, Harvard University. All rights reserved.
        </div>
      </div>
    </div>
  );
};

export default App;
